        [Fact]
        public void TestTextSampleValues()
        {
            string[] expectedEmails = { "email@subdomain.example.com", "firstname+lastname@example.com", "email@example.com", "firstname.lastname@example.com", "email@123.123.123.123" };
            var parser = new Parser(TextSample);
            string[] emails = parser.GetAllEmails();
            
            Assert.Equal(expectedEmails, emails);
        }

